package org.pkislov.task2.controller;import jakarta.validation.Valid;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.pkislov.task2.dto.SignalDto;import org.pkislov.task2.service.SignalService;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RestController;import java.util.List;@Slf4j@RestController@RequiredArgsConstructorpublic class SignalController {    private final SignalService signalService;    @PostMapping(value = "signals")    public ResponseEntity<String> sendSignals(@RequestBody @Valid List<SignalDto> signals) {        log.info("Received request to send signals");        signalService.sendSignalToInputQueue(signals);        return ResponseEntity.ok().body("Signals sent to Input topic");    }}