package org.pkislov.task2.listener;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.pkislov.task2.dto.DistanceDto;import org.springframework.beans.factory.annotation.Value;import org.springframework.kafka.annotation.KafkaListener;import org.springframework.kafka.annotation.TopicPartition;import org.springframework.stereotype.Service;@Slf4j@Service@RequiredArgsConstructorpublic class OutputListeners {    @Value("${spring.kafka.topic.output}")    private String outputTopic;    @KafkaListener(            topicPartitions = @TopicPartition(                    topic = "${spring.kafka.topic.output}",                    partitions = {"0,1,2"}            ),            groupId = "${spring.kafka.consumer.group-id}",            containerFactory = "kafkaListenerContainerFactory")    public void loggingConsumer0(DistanceDto message) {        log.info("Received Message in %s in %s".formatted(message, outputTopic));    }}